m0_db_aggreg = function(db_list){
  i=1
  concatenated_table_m0 = data.frame()
  for (db in db_list) {
      # Connect to the SQLite DB
    con &lt;- dbConnect(RSQLite::SQLite(), db)
    print(paste0("DB en cours de traitement : ", db))

    tables &lt;- dbListTables(con)

    center_fusion = data.frame()

    tables_order = list("consent" ,"sd" ,"pss" ,"asrs" ,"lec5" ,"intro_mini_m0" ,"modulea" ,"moduleb","modulec" ,"moduled" ,"modulef" ,"moduleg" ,"moduleh" ,"modulei0_cust" ,"modulei" ,"modulej" ,"modulek" ,"modulen" ,"modulep" ,"clot")

    for (table in tables_order) {
      if (table == "_goupile"){next}
      data &lt;- dbReadTable(con, table)
      print(paste0("        Table : ", table))
      #colnames(data) &lt;- paste(table, colnames(data), sep = "_")

      #Ajout d'un prefixe aux colonne de creation/modif pour conserver les info
      #de chaque formulaire + gestion des noms de col numérique retransformé par
      #R par l'ajout d'un caractère X en début. On réaffecte le nom du formulaire
      data = data %&gt;% rename_with( ~ if_else(str_starts(., "X__") & . != "X__ROOT", str_c(paste0(table,"_"), .), .), starts_with("X__"))
      data = data %&gt;% rename_with( ~ if_else(str_starts(., "X") & . != "X__ROOT", str_replace(., "X", paste0(table,"_")), .), starts_with("X"))


      # Correction à la volée des erreurs de nom de col à la progra initiale
      if(table == "asrs"){data = data %&gt;% rename(asrs_incarc_impact = pss_incarc_impact)}


      if (nrow(center_fusion) == 0) {
        center_fusion &lt;- data
      } else {
        # Perform a join operation based on the 'X__ROOT' column
        center_fusion &lt;- full_join(center_fusion, data, by = "X__ROOT")
      }
    }
    center_fusion &lt;- center_fusion %&gt;% mutate(across(starts_with("severity_score_"), as.character))
    assign(paste0("synthese_",i),center_fusion)

    concatenated_table_m0 &lt;- bind_rows(concatenated_table_m0,center_fusion)

    # Disconnect from DB
    dbDisconnect(con)
    print(i)
    i=i+1
  }
  return(concatenated_table_m0)
}

