labeling_db = function(database){

  source("QMDtoR/labels/epsylon_labels.R")
  labeled_table &lt;- database
print("test")
# Liste des noms des colonnes à traiter
  col_names = colnames(labeled_table)


  for (col_name in col_names) {

    if (exists(col_name)) {
      if(typeof(get(col_name, mode = "any")) != "closure"){
      # Remplacement des valeurs selon le dictionnaire correspondant
      labeled_table[[col_name]] &lt;- get(col_name)[as.character(labeled_table[[col_name]])]
      }}
    #Remplacement si variable listées comme dichotomique
    else if(col_name %in% list_var_dicho){
      labeled_table[[col_name]] = dicho_answ[as.character(labeled_table[[col_name]])]
    }
  }
  return(labeled_table)
}

